mapper.insert:
INSERT INTO TBL_SCHEDULE(
    ID, SCHEDULE_TITLE, SCHEDULE_CONTENT, SCHEDULE_START_DATE, SCHEDULE_END_DATE,
    SCHEDULE_CREATED_DATE, SCHEDULE_COLOR, SCHEDULE_CATEGORY, SCHEDULE_REPEAT, CALENDAR_ID
)
VALUES (
           SEQ_SCHEDULE.NEXTVAL, #{scheduleTitle}, #{scheduleContent}, #{scheduleStartDate}, #{scheduleEndDate},
           #{scheduleCreatedDate}, #{scheduleColor}, #{scheduleCategory}, #{scheduleRepeat}, #{calendarId}
       )

INSERT INTO TBL_CALENDAR_INVITE_GROUP(
    ID, CALENDAR_INVITE_GROUP_INVITED_MEMBER_ID,CALENDAR_INVITE_GROUP_HOST_ID,CALENDAR_INVITE_GROUP_IS_APPROVED, CALENDAR_ID
)
VALUES (
           SEQ_SCHEDULE.NEXTVAL, #{calendarInviteGroupInvitedMemberId}, #{calendarGroupHostId}, #{calendarInviteGroupIsApproved}, #{calendarId}
       )

mapper.update:
UPDATE TBL_CALENDAR_INVITE_GROUP
SET
    CALENDAR_INVITE_GROUP_IS_APPROVED = #{calendarInviteGroupIsApproved}
WHERE ID = #{id}

mapper.select:
SELECT ID
FROM TBL_CALENDAR
WHERE ID = #{id}

mapper.insert:
INSERT INTO TBL_CALENDAR_MEMBER_GROUP(
