mapper.select:
SELECT COUNT(ID)
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(ID)
FROM TBL_MEMBER
WHERE MEMBER_PHONE = #{memberPhone}

SELECT COUNT(ID)
FROM TBL_MEMBER
WHERE MEMBER_NICKNAME = #{memberNickName}

SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PASSWORD = #{memberPassword}

SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT COUNT(ID)
FROM TBL_MEMBER
WHERE MEMBER_NAME = #{memberName} AND MEMBER_PHONE = #{memberPhone}

SELECT MEMBER_EMAIL
FROM TBL_MEMBER
WHERE MEMBER_NAME = #{memberName} AND MEMBER_PHONE = #{memberPhone}

SELECT COUNT(ID)
FROM TBL_MEMBER
WHERE MEMBER_NAME = #{memberName} AND MEMBER_EMAIL = #{memberEmail}

SELECT ID
FROM TBL_MEMBER
WHERE MEMBER_NAME = #{memberName} AND MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE TBL_MEMBER
MEMBER_EMAIL = #{memberEmail}, MEMBER_PASSWORD = #{memberPassword}, MEMBER_NAME = #{memberName}, MEMBER_BIRTH = #{memberBirth}, MEMBER_GENDER = #{memberGender}, MEMBER_PHONE = #{memberPhone}, MEMBER_NICKNAME = #{memberNickName}, MEMBER_STATUS_MESSAGE = #{memberStatusMessage}, MEMBER_IMG_NAME = #{memberImgName}, MEMBER_IMG_PATH = #{memberImgPath}, MEMBER_POINT = #{memberPoint}, MEMBER_PROVIDER = #{memberProvider}
WHERE ID = #{id}

UPDATE TBL_MEMBER
SET MEMBER_PASSWORD = #{memberPassword}
WHERE ID = #{id}
