mapper.delete:
DELETE FROM TBL_SCHEDULE_MEMBER_GROUP
WHERE ID IN (
    SELECT SCHEDULE_MEMBER_GROUP_ID
    FROM TBL_SCHEDULE
    WHERE CALENDAR_ID = #{calendarId}
)

DELETE FROM TBL_SCHEDULE_GROUP_MEMBER
WHERE SCHEDULE_MEMBER_GROUP_ID IN (
    SELECT SCHEDULE_MEMBER_GROUP_ID
    FROM TBL_SCHEDULE
    WHERE CALENDAR_ID = #{calendarId}
)

DELETE FROM TBL_SCHEDULE_GROUP_MEMBER
WHERE SCHEDULE_MEMBER_GROUP_ID = (
    SELECT SCHEDULE_MEMBER_GROUP_ID
    FROM TBL_SCHEDULE
    WHERE ID = #{scheduleId}
)

DELETE FROM TBL_SCHEDULE_MEMBER_GROUP
WHERE ID = (
    SELECT SCHEDULE_MEMBER_GROUP_ID
    FROM TBL_SCHEDULE
    WHERE ID = #{scheduleId}
)
